sp_widget.client_script:
function($rootScope,$scope,spModal) {
	/* widget controller */
	var c = this;
	c.selectItem = function(sysId) {
		var id = sysId;
		console.log('Task ID: ' + id);
		$rootScope.taskID = id;
		$rootScope.$emit('selectTask', id);
	}

	c.newItem = function() {
		console.log('New Task');
		$rootScope.$emit('newTask');
	}

	c.newCategory = function() {
		console.log('New Category');
		$rootScope.$emit('newCategory');
	}

	c.completeTask = function(sysId) {
		spModal.confirm("Are you sure you want to mark this task as complete?").then(function(confirmed){
			if(confirmed){
				var id = sysId;
				console.log('Complete Task Id: ' + id);
				c.server.get({
					action: 'completeTask',
					taskID: id
				}).then(function(){
						location.reload();
				});
			}
		})
	}

	c.deleteCategory = function(sysId,value) {
		spModal.confirm("Are you sure you want to delete this category? Any open tasks in this category will be moved to General.").then(function(confirmed){
			if(confirmed){
				var id = sysId;
				console.log('Remove Category Id: ' + id);
				c.server.get({
					action: 'deleteCategory',
					categoryID: id,
					categoryValue: value
				}).then(function(){
					location.reload();
				});
			}
		})
	}

	c.selectUser = function(user) {
		console.log(user);
//		document.getElementById("id").style.opacity = "..."
	}

	c.allowDrop = function(ev) {
		ev.preventDefault();
	}

	c.drag = function(ev) {
		ev.dataTransfer.setData("text",ev.target.id);
	}

	c.drop = function(ev) {
		ev.preventDefault();
		if (!ev.target.getAttribute("ondrop")) {
			return false;
		}
		var data = ev.dataTransfer.getData("text");
		ev.target.appendChild(document.getElementById(data));

		c.server.get({
			action: 'moveTask',
			taskID: data,
			hostID: ev.target.id
		})
	}

}

