sp_widget.client_script:
function($scope,$rootScope,$uibModal,spUtil) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectTask', function(event,data) {
		c.server.get({
			action: 'getTask',
			taskID: $rootScope.taskID
		}).then(function(r) {
			c.data.title = r.data.task.title;
			c.data.description = r.data.task.description;
			c.data.category = r.data.task.category;
			c.data.assigned_to = r.data.task.assigned_to;
			c.data.assigned_to_id = r.data.task.assigned_to_id;
			c.data.priority = r.data.task.priority;
			c.data.taskID = r.data.task.sys_id;
		}).then(function() {
			$scope.user = {
				displayValue: c.data.assigned_to,
				value: c.data.assigned_to_id,
				name: 'user'
			};
		}).then(function() {
			c.modalInstance = $uibModal.open({
				templateUrl: 'modalTemplate',
				scope: $scope
			});
		});
	});

	c.updateTask = function(updateType) {
		c.server.get({
			action: 'updateTask',
			taskID: c.data.taskID,
			taskDescription: c.data.description,
			taskTitle: c.data.title
		}).then(function(r) {
		});
	}

	$scope.$on("field.change", function(evt, parms) {
		if (parms.field.name == 'user')
			c.data.setUser = parms.newValue;

		c.server.update().then(function(response) {
			spUtil.update($scope);
		})
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.taskID) {
		var task = new GlideRecord('x_380098_gtd_tasks');
		if (task.get(input.taskID)) {
			if (input.action == 'getTask') {
				data.task = {};
				$sp.getRecordDisplayValues(data.task, task, "title,category,assigned_to,description,sys_id");
				data.task.priority = task.getValue('priority');
				data.task.assigned_to_id = task.getValue('assigned_to');
			}
			else if (input.action == 'updateTask') { 
				task.title = input.taskTitle; 
				task.description = input.taskDescription; 
				task.update(); 
			}
		}
	}
})();
